-- CREATE TABLES

-- DROP TABLE report_definitions;
CREATE TABLE report_definitions
(
  id bigserial NOT NULL,
  "name" character varying NOT NULL,
  definition character varying NOT NULL,
  created timestamp without time zone DEFAULT now(),
  createdby character varying,
  alteredby character varying,
  lastupdated timestamp without time zone DEFAULT now(),
  usercompanyid bigint NOT NULL,
  test_xml character varying,
  CONSTRAINT report_definitions_pkey PRIMARY KEY (id),
  CONSTRAINT report_definitions_name_unique UNIQUE (name, usercompanyid)
);

-- DROP TABLE report_parts;
CREATE TABLE report_parts
(
  id bigserial NOT NULL,
  "name" character varying NOT NULL,
  "value" character varying NOT NULL,
  created timestamp without time zone DEFAULT now(),
  createdby character varying,
  alteredby character varying,
  lastupdated timestamp without time zone DEFAULT now(),
  usercompanyid bigint NOT NULL,
  CONSTRAINT report_parts_pkey PRIMARY KEY (id),
  CONSTRAINT report_parts_name_unique UNIQUE (name, usercompanyid)
);

