DROP TABLE IF EXISTS edi_transactions_log_history;

CREATE TABLE edi_transactions_log_history
(
  id bigserial,
  edi_transactions_log_id bigint NOT NULL,
  "name" character varying NOT NULL,
  status character varying NOT NULL,
  message character varying,
  external_system_id bigint,
  data_definition_id bigint NOT NULL,
  usercompanyid bigint NOT NULL,
  created timestamp without time zone DEFAULT now(),
  createdby character varying,
  alteredby character varying,
  lastupdated timestamp without time zone DEFAULT now(),
  internal_id bigint,
  internal_identifier_field character varying,
  internal_identifier_value character varying,
  external_id character varying,
  "action" character varying NOT NULL,
  CONSTRAINT edi_transactions_log_history_pkey PRIMARY KEY (id),
  CONSTRAINT edi_transactions_log_history_fkey FOREIGN KEY (edi_transactions_log_id)
      REFERENCES edi_transactions_log (id) MATCH SIMPLE
      ON UPDATE NO ACTION ON DELETE CASCADE
);

ALTER TABLE edi_transactions_log_history OWNER TO "www-data";

  SELECT 'ediinterface', 'M', m.location||'/models/EDIInterface.php', id
    FROM modules m
   WHERE m.name = 'edi';

  SELECT 'editransactionloghistory', 'M', m.location||'/models/EDITransactionLogHistory.php', id
    FROM modules m
   WHERE m.name = 'edi';

